@font-face {
	font-family: 'Conv_Montserrat-Bold';
	src: url('../../../fonts/Montserrat-Bold.eot');
	src: local('☺'), url('../../../fonts/Montserrat-Bold.woff') format('woff'), url('../../../fonts/Montserrat-Bold.ttf') format('truetype'), url('../../../fonts/Montserrat-Bold.svg') format('svg');
	font-weight: normal;
	font-style: normal;
}

@font-face {
	font-family: 'Conv_Montserrat-Regular';
	src: url('../../../fonts/Montserrat-Regular.eot');
	src: local('☺'), url('../../../fonts/Montserrat-Regular.woff') format('woff'), url('../../../fonts/Montserrat-Regular.ttf') format('truetype'), url('../../../fonts/Montserrat-Regular.svg') format('svg');
	font-weight: normal;
	font-style: normal;
}

.sign-in-form {
    position: absolute;
    width: 380px;
    height: 358px;
    left: 560px;
    top: 701px;

    background: #FFFFFF;
    border: 1px solid rgba(0, 0, 0, 0.12);
    box-sizing: border-box;
    box-shadow: 0px 0px 25px rgba(0, 0, 0, 0.2);
    border-radius: 4px;

    .p1 {
        position: absolute;
        padding: 0px;
        margin: 0px;
        width: 320px;
        height: 60px;
        top: 40px;
        left: 30px;
    
        font-family: Conv_Montserrat-Bold;
        font-style: normal;
        font-weight: bold;
        font-size: 24px;
        line-height: 30px;
        color: #1F2041;
    }

    input {
        position: absolute;
        width: 320px;
        height: 44px;
        left: 30px;
        top: 90px;

        padding: 10px;

        background: #FFFFFF;
        border: 1px solid rgba(31, 32, 65, 0.25);
        box-sizing: border-box;
        border-radius: 4px;
    }

    input:hover {
        border: 1px solid rgba(31, 32, 65, 0.5);
    }

    input:hover::placeholder {
        color: rgba(31, 32, 65, 0.75);
    }

    input::placeholder {
        font-family: Conv_Montserrat-Regular;
        font-style: normal;
        font-weight: normal;
        font-size: 14px;
        line-height: 24px;
        color: rgba(31, 32, 65, 0.25);
    }

    button {
        position: absolute;
        display: flex;
        width: 320px;
        height: 44px;
        left: 30px;
        top: 208px;

        cursor: pointer;

        background: linear-gradient(180deg, #BC9CFF 0%, #8BA4F9 100%);
        border-radius: 22px;
        border-style: none;

        p {
            width: 280px;
            height: 15px;
            margin-top: 14px;
            margin-left: 34px;

            font-family: Conv_Montserrat-Regular;
            font-style: normal;
            font-weight: bold;
            font-size: 11px;
            line-height: 15px;

            text-align: center;
            text-transform: uppercase;

            color: #FFFFFF;
        }

        .arrow {
            width: 44px;
            height: 34px;
            margin-top: 9px;
            margin-right: 4px;

            font-family: 'Material Icons';
            font-weight: normal;
            font-style: normal;
            font-size: 24px;
            line-height: 24px;
            text-transform: none;
            letter-spacing: normal;
            word-wrap: normal;
            white-space: nowrap;
            direction: ltr;
            text-align: end;

            color: #FFFFFF;

            /* Support for all WebKit browsers. */
            -webkit-font-smoothing: antialiased;
            /* Support for Safari and Chrome. */
            text-rendering: optimizeLegibility;
            /* Support for Firefox. */
            -moz-osx-font-smoothing: grayscale;
        }
    }

    .toxin {
        position: absolute;
        width: 164px;
        height: 18px;
        left: 30px;
        top: 281px;

        font-family: Conv_Montserrat-Regular;
        font-style: normal;
        font-weight: normal;
        font-size: 14px;
        line-height: 18px;
        
        color: #000000;
    }

    .toxin-button {
        position: absolute;
        width: 99px;
        height: 44px;
        left: 251px;
        top: 282px;
        padding: 2px;

        border-radius: 22px;
        color: #122dff;

        p {
            padding: 1.1em 1.6em;
            margin: 0;
            font-family: Conv_Montserrat-Regular;
            font-style: normal;
            font-weight: bold;
            font-size: 11px;
            line-height: 15px;
            border-radius: 22px;

            text-align: center;
            text-transform: uppercase;

            color: #BC9CFF;
            background: #FFFFFF;
        }
    }
}
